    class Mod_ChangeToneBrust extends FirmwareMod { //lets hope it will work. I duno Py nor JS, just modding stuff 4 people 2 help
        constructor() {
            super("Repeater Tone Burst", "O botão F2 [Flashlight] + PTT ao mesmo tempo envia um tom de ativação de 1750 Hz por padrão para os repetidores na UE. Para desativar os canais NOAA, é necessário um tom de 1050 Hz. Outras frequências de pulso de tom de repetidor não tão comuns são 1000Hz, 1450Hz, 1750Hz, 2100Hz", 0);
            this.contrastValue = addInputField(this.modSpecificDiv, "Digite um novo valor de Tone Burst em Hz de 1000-3950:", "1750");
        }

        apply(firmwareData) {
            const minValue = 1000;
            const maxValue = 3950;
            const inputValue = parseInt(this.contrastValue.value);

            if (!isNaN(inputValue) && inputValue >= minValue && inputValue <= maxValue) {
                const newData = new Uint8Array([inputValue]);
                firmwareData = replaceSection(firmwareData, newData, 0x29cc);
                log(`Sucesso: ${this.name} aplicado.`);
            }
            else {
                log(`ERROR in ${this.name}: Repeater Tone Burst must be a Tone Freq. in Hz from 1000-3950 Hz!`);
            }
            return firmwareData;
        }
    }
